import React, { useEffect, useState } from 'react';
import './Note.css';

function Note({ note, deleteNote }) {
  const [boxSize, setBoxSize] = useState({ width: 250, height: 250 });
  const currentDate = new Date().toLocaleString();

  useEffect(() => {
    const contentLength = note.text.split(' ').length;
    if (contentLength > 100) {
      setBoxSize({ width: 400, height: 300 });
    }
  }, [note.text]);

  return (
    <div className="note" style={{ width: boxSize.width, minHeight: boxSize.height }}>
      <div className="content">
        <p>{note.text}</p>
      </div>
      <div className="footer">
        <p className="date">{currentDate}</p>
        <button className="delete-button" onClick={() => deleteNote(note.id)}>Delete</button>
      </div>
    </div>
  );
}

export default Note;
