import React, { useState } from 'react';
import './NoteForm.css';
import axios from 'axios';

function NoteForm({ addNote }) {
  const [text, setText] = useState('');
  const [isSubmitting, setIsSubmitting] = useState(false); // Add state to track form submission

  const handleChange = (e) => {
    setText(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!text.trim() || isSubmitting) return; // Prevent multiple submissions
    setIsSubmitting(true); // Set submitting state to true

    try {
      await axios.post('http://localhost:3000/notestable', { text: text });
      addNote(text);
      setText('');
      console.log('Note added successfully');
    } catch (error) {
      console.error('Error adding note:', error);
    } finally {
      setIsSubmitting(false); // Reset submitting state
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input type="text" value={text} className="input-field" onChange={handleChange} placeholder="Enter your note" />
      <button type="submit" disabled={isSubmitting}>Add Note</button> {/* Disable button while submitting */}
    </form>
  );
}

export default NoteForm;
