import React, { useState, useEffect } from 'react';
import './App.css';
import NoteList from './components/NoteList';
import NoteForm from './components/NoteForm';
import Header from './components/Header';
import Footer from './components/Footer';
import axios from 'axios';

function App() {
  const [notes, setNotes] = useState([]);

  // Function to fetch notes from the API and set the state
  const fetchNotes = async () => {
    try {
      const response = await axios.get('http://localhost:3000/notestable');
      setNotes(response.data);
    } catch (error) {
      console.error('Error fetching notes:', error);
    }
  };

  useEffect(() => {
    fetchNotes(); // Fetch notes when the component mounts
  }, []); // Empty dependency array ensures the effect runs only once

  const addNote = async (text) => {
    try {
      const response = await axios.post('http://localhost:3000/notestable', { text });
      console.log("Entered successfully");
      setNotes([...notes, response.data]); // Update state with the new note
    } catch (error) {
      console.error('Error adding note:', error);
    }
  };

  const deleteNote = async (id) => {
    try {
      await axios.delete(`http://localhost:3000/notestable/${id}`);
      setNotes(notes.filter((note) => note.id !== id));
    } catch (error) {
      console.error('Error deleting note:', error);
    }
  };

  return (
    <div className="App">
      <Header />
      <NoteForm addNote={addNote} />
      <NoteList notes={notes} deleteNote={deleteNote} />
      <Footer />
    </div>
  );
}

export default App;
